function igk_create_module($path){
	$v_k = "sys://require_mods";
	$g = igk_get_env($v_k, array());

	if (isset($g[$path]))
		return 0;

	$dir = IGK_LIB_DIR . "/Modules/{$path}";
	igk_wln($dir);
	if (file_exists($dir)){
		return 0;
