///<summary>get system directory presentation</summary>
function igk_io_dir($dir){
	$d = DIRECTORY_SEPARATOR;
	$r = '/';
	$out = IGK_STR_EMPTY;

	if (ord($d) == 92)
	{
		$out =preg_replace("/\//", '\\', $dir);
		$out = str_replace("\\\\", "\\", $out);
	}
	else{
		$out =preg_replace('/[\\\]/', '/', $dir);
		$out = str_replace("//", "/", $out);
	}
	return $out;
}
