///<summary>get a list of include files that have a BOM header. testing functions</summary>
function igk_io_get_wbom_files(){
$i = 0;
$t = array();
foreach(get_included_files() as $k=>$v){
	if (filesize($v)<3)
		continue;
	//igk_wln($v);
	$txt = igk_io_read_allfile($v);//ff($v);

	if ((ord($txt[0]) === 239)&&
	(ord($txt[1]) === 187)&&
	(ord($txt[2]) === 191)
	)
	{
		$i++;
		$t[] = $v;
		igk_wln($v);
	}
}
return $t;
}
