///sample: script_name = /local.com/index.php
///output: /data/sample/
function igk_io_request_entry(){
	$b = igk_io_request_uri();
	$t = dirname(igk_getv($_SERVER, "SCRIPT_NAME") ?? igk_getv($_SERVER, "PHP_SELF"));
