///<summary>convert string litteral to string html text presentation<summary>
function igk_html_from_string($str,$block="p"){
	$tstr = explode("\n", $str);
	$t= igk_createNode("div");
	foreach($tstr as $k=>$v){
		$g = trim($v);
		if (empty($g)){
			$t->addBr();
		}else
		$t->add($block)->setContent(trim($v));
	}
	return $t->Render();

}

