function igk_zip_unzip_entry($f, $entry){
	$c = "zip://".$f."#".$entry;
	$h = fopen($c,'r');
	if (!$h){
		// igk_debug_die("can't unzip file entry {$f}");
		return null;
	}
	$c = "";
	while (!feof($h)) {
