function igk_get_value($obj, $path){
	$c = explode("/", $path);
	$o = null;
	while($obj && (igk_count($c)>0)){
		$g =null;
		$n = array_shift($c);
		if (is_object($obj)){
			$g = $obj->$n;
		}else if (is_array($obj)){
			if (!isset($obj[$n])){
				igk_die("faile to ge value ".$n);
			}
			$g = $obj[$n];
		}
		$obj=$g;
		$o = $g;
	}

	return $obj;
}

