function igk_str_explode($array, $str)
{ //TODO : avoid recursion ovoid
	$t = array();
	if(count($array)>0)
	{
		$k = explode($array[0], $str);
		$array = array_slice($array, 1);
		foreach($k as $kt){
			$t = array_merge($t, igk_str_explode($array, $kt));
		}
	}
	else{
		$t[] = $str;
	}
	return $t;
}

