function igk_css_ob_get_tempfile($f, & $from=null){

	$vtemp = new IGKHtmlDocTheme(igk_app(), "theme://inline/tempfiles");

	igk_set_env(IGK_CSS_TEMP_FILES_KEY,1);
	IGKOb::Start();
	igk_css_bind_file(null,$f,$vtemp);
