	}
	return 1;
}


function igk_css_get_map_selector(){
	$igk = igk_app();
	$selector = array();

	foreach($igk->Doc->SysTheme->def->Attributes as $k=>$v){

		$tab = explode(',', $k);
		if (empty($v))
			continue;
		foreach($tab as $s=>$t)
		{

			if (!empty($t))
			{
				$selector[trim($t)] = $v;
			}
		}
	}

	foreach($igk->Doc->Theme->def->Attributes as $k=>$v){

		$tab = explode(',', $k);
		if (empty($v))
			continue;
		foreach($tab as $s=>$t)
		{

			if (!empty($t))
			{
				$selector[trim($t)] = $v;
			}
		}
	}
	return $selector;
}
