///<summary>remove base uri</summary>
function igk_html_rm_base_uri($v){
	$s = igk_io_baseUri();
	$rg = "/((".str_replace("/","\\/", $s)."(\/)?)+)/i";
	if (preg_match($rg, $v)){
		//remove domain name
		$v = preg_replace($rg, IGK_STR_EMPTY, $v);
	}
	return $v;
}

