	$t = igk_createNode($target);
	if (is_object($mix)){
		foreach($mix as $k=>$v){
			$t->add($item)->setClass("{$k}")->Content = $v;
		}
	}
	else if (is_array($mix)){
		foreach($mix as $k=>$v){
			$t->add($item)->setClass("i i-{$k}")->Content = $v;
