///<summary>copy filter dataobj value value only . erase the src data attributes if key not found in data object </summay>
function igk_db_copy_row(&$src, $dataobj, $erase=1){
	foreach($src as $k=>$v){
		if (isset($dataobj->$k)){
			$src->$k = $dataobj->$k;
		}else{
			//igk_wln("not found ".$k);
			if ($erase)
			$src->$k = null;
		}
	}
}

