///<summary>check if a file is included</summary>
function igk_file_IsNotIncluded($file)
{
	$tab = get_included_files();
	return $tab[0] == $file;
}

function igk_file_IsDirectChildOf($file, $parentFile)
{
	$tab = get_included_files();
	$c = igk_count($tab)-1;
	$r = 0;
	while($c>0){
		if (!$r){
			$r = ($tab[$c] == $file);
		}
		else{
			$tab[$c] == $parentFile;
			return 1;
		}
		$c--;
	}
	return 0;

	// if ($c>1){
		// return ($tab[$c-1] == $file) && $tab[$c-2] == $parentFile;
	// }
	// return 0;
}


