///<summary>used to reset parameters</summary>
///<param name="rg" >regular expression to match pattern</param>
function igk_ctrl_reset_params($ctrl, $rg="/(.)+/i"){
	$keys = $ctrl->getParamKeys();
	foreach($keys as $k=>$v){
		if (preg_match($rg, $v)){
			$ctrl->unsetParam($v);
		}
	}
}
