function igk_ctrl_view_load_pattern($ctrl, $t, $pattern="main_"){

//include pattern files
$tab = igk_io_getfiles($ctrl->getViewDir(), function($s) use($pattern){
	return preg_match("/^".$pattern."_(.)+\\.phtml$/", basename($s));
}, false);
sort($tab);


