///<summary>convert value to presentation</summary>
function igk_get_sizev($v,$round=4){
$sm_sizeFormat = array(
		"Tb"=>1099511627776,
		"Gb"=>1073741824,
		"Mb"=>1048576,
		"Kb"=>1024,
		"B"=>1
	);

	if ($v==0)
			return "0 byte";
		foreach($sm_sizeFormat as $k=>$vv){
			if ($v>$vv){
				return round(($v/$vv), $round)." ".R::gets("enum.memoryunit.".$k, $k);
			}
		}
		return "0 byte";
}


