function igk_json_value($n){
		if (preg_match_all("/^(?P<delimiter>('|\"))(?P<key>(.)+)\\1$/i", $n,$tab)){
			//igk_wln($tab);
			return $tab["key"][0];
		}
		return $n;

}


///<summary>parse expression. multi json object expression</summary>
///<param name="exp">param or semi column expression</param>
function igk_json_array_parse($exp, & $err=null){

	$t = array();//out array
	$st = array();//push state
	$eof = 0;
	$c =0; //position
	$ln=strlen($exp);
	$rm = 0; // read mode
	$q = null;
	$v = "";
	$_br=0;
	$k = "";
	$err = array();
	$decimal_regex = "/^(?P<_dec>([0-9]+(\\.|\\.([0-9]+)){0,1}))$/i";
	$identifier_regex = "/^(?P<_identier>(null|((_|[a-z])([0-9a-z_])*)))$/i";

	while(!$eof && ($ln>$c)){
		$ch = $exp[$c];
		if ($rm==0x18){
			if ($ch==","){
				if ($q){
					$rm=0x2;//read key
				}
			}
			$c++;
			continue;
		}
		switch($ch){
			case "{"://sub string
				if ($rm == 0){
				//	igk_wln("create obj");
					$q = igk_createObj();
					$t[] = $q;
					$rm=0x2;
					$v ="";
				}else if (($rm & 0x1) == 0x1){
					$v.=$ch;
				}else if ($rm ==0x2){
					//bad json
					$err[] = ("bad json .... 0x2");
					// igk_wln($err);
					// igk_wln($t);
					return null;
				}
				else {

					if ($rm == 0x4){ //at non litteral value
						$q->$k = igk_createObj();
						array_unshift($st, $q);
						$q = $q->$k;
						$rm=0x2;
						$v ="";

					}else {
						$err[] = "Not read litteral : read mode : ".$rm;
						return null;
					}
				}
			break;
			case "}":
				if (($rm & 0x1) == 0x1){
					$v.=$ch;
				}else{
					if ($rm == 0x10){
						// igk_wln($q);
						$q = array_shift($st);
						if ($q == null){
							$rm = 0;
						}
						// igk_wln($q);
						//$rm=0;
					}
					else if ($rm ==0x4){

						if ($v!=null){
							 if (empty($v)){
								$err[] = "empty value ";
								// igk_wln($err);
								// igk_wln($t);
								return null;
							}
							//end read value non litteral
							$decimal = preg_match($decimal_regex,$v);
							if ($decimal && $v[strlen($v)-1]==".")
								$v .="0";

						}
						$q->$k= $v;
						$v="";
						$k="";


						// igk_wln($q);
						// igk_wln("count :::: ".count($st));
						$rm = 0x10;
						$q = array_shift($st);
						if ($q == null){
							$rm= 0;
						}

					}else {
						if ($rm!=0){
							$err[] = "possible not catchable object";
							return $t;
						}
					}
				}
				break;
			case "[":
// igk_wln("star t read ...".$rm);

				if (($rm == 0) || ($rm== 0x04)){
					$iv = igk_str_read_brank($exp,$c);
					$iv = trim($iv);
// igk_wln("iv ".$iv);
					if (!empty($iv)){
						// igk_wln("read array!!!!!");
						$ttab  = igk_json_readarray($iv);

						if ($rm ==0){
							$q=$ttab;
							return $q;
						}
						else{
							$q->$k = $ttab;
							$rm = 0x1;
						}
					}
				}else
				$rm = 0x08;
				//$v .= $ch;
				break;
			default:
					switch($rm){
						case 0x2: //read key

							if (($ch=="\"")||($ch=="'")){
								$_br = $ch;
								$rm=0x3;
								//e_xit;
							}else{
								if ($ch==':'){
									//finnish readin key e
									if (empty($v))return null;
									$k = $v;
									$rm= 0x4; //start reading value
									$v="";

									//igk_wln("key : ".$k);

								}else{
									$v = trim($v.$ch);
								}
							}
							break;
						case 0x3: //read key litteral
							if (($ch!= $_br) ||  (($ch==$_br) && ($c>0) && ($exp[$c-1]=="\\"))){
								$v = $v.$ch;
								//igk_wln($v);
							}else{
								//litteral end
								// igk_wln("en read ".$v);
								$k = $v;
								$q->$k=null;
								$rm=0x21;
								$v ="";
							}
							break;
						case 0x4: //start ready value
							if (($ch==="\"")||($ch==="'")){
								$_br = $ch;
								$rm=0x05;
							}else if ($ch=="["){
								$rm = 0x08; //start read array
							}else {

								$t_s = trim($v.$ch);
								if ( ($ch != ',' ) && (empty($t_s) || preg_match($identifier_regex, $t_s) || preg_match($decimal_regex,$t_s))){
									// igk_wln("match ====================".$ch. " : ".$t_s);
									if ($t_s=='null'){
										$v = null;
									}else
										$v = $t_s;
								}
								else{
									$rm = 0x2;
									if($v!=null){
									//end read value non litteral
									$decimal = preg_match($decimal_regex,$v);
									if ($decimal && $v[strlen($v)-1]==".")
										$v .="0";
									}
									$q->$k= $v;
									$v="";
									$k="";
									// igk_wln("normal");
									// igk_wln($q);
								}
							}
							break;
						case 0x5: //reading value litteral
							if (($ch!= $_br) ||  ($ch==$_br && ($c>0) && ($exp[$c-1]=="\\"))){
								$v = $v.$ch;
							}else{
								// igk_wln("second value ".$v);
								$q->$k= $v;
								$rm=0x10;
								$v="";
								$k=null;
								// igk_wln($q);
								//e_xit;
							}
							break;
						case 0x8: //reading value array
							$iv = "";
							$iv = igk_str_read_brank($exp,$c);
							$iv = trim($iv);
// igk_wln("read array 2");
							if (!empty($iv)){
								$ttab  = igk_json_readarray($iv);

								$q->$k=$ttab;
							}
							$rm	=0x18;
							$v="";
							break;
						case 0x10: //splitter
							if ($ch == ","){
								//start reading key
								$rm  = 0x2;
							}
							break;
						case 0x21: //wait for ready value
							if ($ch==':')
								$rm = 0x4;
							break;
					}
				break;

		}
		$c++;

		if ($eof)
			break;
	}
	return $t;
}

function igk_json_readarray($v){

	$rm = 0;
	$ch = 0;
	$pos = 0;
	$ln = strlen($v);
	$iv = "";
	$start= 0;
	$tbalue = array();

	while($pos<$ln){
		$ch = $v[$pos];
			switch($ch){
				case ",":
					if ($rm=0x11){
						$rm=0;
					}else{
						$iv.=$v;
					}
					break;
				case "\"":
				case "'":
				if ($rm == 0){
					$rm=1;//read value
					$start= $ch;
					$iv="";//$ch;
				}else if ($rm==1){
					if (($ch!==$start) || $v[$pos-1]=="\\"){//escape t
						$iv.=$ch;
					}else{
						$tbalue[] = $iv;//.$ch;
						$iv="";
						$rm=0x11;
					}
				}
				break;
				case "{"://start json data
					if ($rm==0){
						$c = igk_str_read_brank($v,$pos,"}","{");
						$obj = igk_json_parse($c);
						if ($obj)
							$tbalue[] = $obj;
						$iv = "";
					}
					else
						$iv.=$ch;
				break;
				case "]"://detect end
					$_cd= trim($iv);
					// igk_wln("end detected ".$_cd);
					if (!empty($_cd)){
						$tbalue[] = $_cd;
					}
					$iv=null;
					break;
				default:
					$iv.=$ch;
					break;
			}

		$pos++;
	}
	return $tbalue;
}
