function igk_is_callback_obj($c){
	$s = IGK_OBJ_TYPE;

	//igk_wln("is obj ".is_object($c) ." : ". is_callable($c) );

	return (is_array($c) && isset($c[$s]) && ($c[$s]=="_callback")) ||
