	{
		$n =IGK_STR_EMPTY;
		switch($n)
		{
			case "c":
				$n = "initconfirm";
				break;
			case "f":
			default:
				$n = "init";
				break;
		}
		return  igk_get_string_format("IGK.winui.frameBox.{$n}({0}],{1});",
		igk_getsv($this->owner->Width?'"'.$this->owner->Width.'"':null,'null'),
