	public function getDefineNS(& $out, $options=null){
		$ns = $this->getFlag(IGK_DEFINEDNS_FLAG);
		if ($options && (igk_count($ns)>0)){
			if (!isset($options->ns))
				$options->ns = array();
		}
		$m = 0;
		$ns = $this->getFlag(IGK_DEFINEDNS_FLAG);//$this->m_namespaces
		if ($ns)
		foreach($ns as $k=>$v){
			if (isset($options->ns) && (igk_getv($options->ns, $k) == $v))
				continue;
			if ($m)
				$out.= " ";
			if ($k=="@global"){
				$out .="xmlns=\"".$v."\"";
			}else{
				$out .="xmlns:{$k}=\"".$v."\"";
			}
			$m=1;
			$options->ns[$k]=$v;
		}
