		{
			if (IGKString::StartWith($name, 'igk:'))
			{		
				$f = igk_createNode(substr($name, 4),null, $param);
				if ($f)
					return $f;
			}
			$tb = explode(':', $name);
			if (count($tb) == 1){
				return new IGKHtmlItem($name);
			}
			//get ns and create
		}
		return null;
	}


