	public function addTableBuild($itemorarray, $filter = null)
	{
		$p = $this->addDiv();
		if (is_array($itemorarray) || is_object($itemorarray))
		{
			$tab = $p->addTable();
			$tr = $tab->addTr();
			$tr->add("th")->Content = "Key";
			$tr->add("th")->Content = "Value";
			foreach($itemorarray as $k=>$v)
			{
				$tr = $tab->addTr();
				$tr->addTd()->Content = $k;
				$tr->addTd()->Content = $v;
			}
		}
		else{
			$p->Content = $itemorarray;
		}
		return $p;
	}


