		$g = $this->getFlag(self::IGK_DOC_TITLE_FLAG);
		if ($value ===null) {
			
			igk_html_rm($g);
			$this->setFlag(self::IGK_DOC_TITLE_FLAG, null);
			return;
		}
		if ($g === null)
		{
			$g = $this->m_head->add("title");
			$this->setFlag(self::IGK_DOC_TITLE_FLAG, $g);
		}
		
		igk_html_add($g, $this->m_head);		
		$g->Index = -9999;
		$g->Content = $value;
		return $this;
	}
