	public function addColor($cl, $value)
	{
		$changed = false;
		if (isset($this->cl[$cl]))
		{
			if ($this->cl[$cl] != $value)
			{
				$this->cl[$cl] = $value;
				$changed = true;
			}
		}
		else{
			$this->cl[$cl] = $value;
			$changed = true;
		}
		if ($changed)
		{
			$this->save();
		}
	}
	public function removeColor($cl)
	{
		if (isset($this->cl[$cl]))
		{
			$this->cl[$cl] = null;
			$this->save();
		}
	}
	public function ClearFont()
	{
		$tab=  $this->ft->Attributes;
		if (count($tab)> 0)
		{
			foreach($tab as $k=>$v)
			{
				//igk_wln($v);
				if (is_object($v)){
					foreach($v->Fonts as $m=>$n){
						$f = igk_io_baseDir($n->File);
						if (file_exists($f))
							@unlink($f);
					}
				}
				else{
					$f = igk_io_baseDir($v);
					if (file_exists($f))
						@unlink($f);
				}
			}
			$this->ft->Attributes->Clear();
			$this->save();
		}
	}
	///<summary>add font package</summary>
	public function addFont($name, $path)
	{
		$changed = false;
		$ft  = & $this->def->getFont();
		if (isset($ft[$name]))
		{
			$changed = $ft[$name] != $path;
			$ft[$name] = $path;
		}
		else{
			$ft[$name] = $path;
			$changed = true;
		}		
	}
	///remove a specific font
	public function removeFont($name)
	{
		$f = $this->ft[$name];
		if ($f )
		{
			if (is_object($f))
			{

				$this->ft[$name] = null;
				unset($this->ft[$name]);
				$this->save();
				return true;
			}

			if (is_string($f))
			{
			$f = igk_io_currentRelativePath($f);

			if (file_exists($f) && (unlink($f)))
			{
				igk_notifyctrl()->addMsg(R::gets("msg.fontfile.removed"));

			}
				$this->ft[$name] = null;
					$this->save();
					return true;
			}
		}
		return false;
	}
