	/// AddImgLnk add image link
	public static function AddAnimImgLnk($target, $uri, $imgname, $width="16px", $height="16px", $desc=null, $attribs=null)
	{
		if (is_object($target))
		{
			$a  = $target->add("a", array("href"=>$uri, "class"=>"img_lnk"));
			$t = array();
			$t["a"] = $a;
			$t["img"] = $a->add("igk-anim-img",
