			$fc = igk_getv($arguments, 0);
			$n = substr($name, 0, strlen($name) -8);
			if (!empty($n) && (strtolower($n) != "callback")){
				$s = call_user_func_array(array($this, $n), array_slice($arguments, 1));
				if ($s !==null){
