				}
				return $s;
			}

		}
		$msg = "/!\\ DBUtility[".get_class($this)."] Action {$name} not implements";
		igk_ilog($msg, __CLASS__);
		igk_notifyctrl()->addError($msg);
		igk_assert_die(igk_server_is_local(), $msg);
