	{
		$this->dieNotConnect();
		$query = IGKSQLQueryUtils::GetUpdateQuery($tbname, $entry, $where, $querytabinfo);
		$s = $this->sendQuery($query);
		return $s;
	}
	public function tableExists($tablename){
		if (empty($tablename))
			return false;
		try{
			$s = $this->sendQuery(
			"SELECT Count(*) FROM `".igk_mysql_db_tbname($tablename)."`", false);
			return $s!==null;
		}
		catch(Exception $ex){
			return false;
		}
		return true;
	}

