		$this->dieNotConnect();
		$query = IGKSQLQueryUtils::GetDeleteQuery($tbname, $entry);
		//igk_wln("delete query  $query");
		$t = $this->sendQuery ($query, $tbname);
		if ($t){

			return true;
		}
		return false;
	}
