	protected function __AcceptRender($o=null){
		$this->ClearChilds();
		$c = igk_getctrl($this->Ctrl);
		if($c == null)
			return false;
		$v_opts = $this->Ctrl->getControllerConfigOptions();
		$v_opts->Index = -1000;
		$this->add($v_opts);
		return parent::__AcceptRender($o);
	}
}

