			igk_notification_push_event(IGK_NOTIFICATION_INITTABLE, $this, array($v_tablename, $v_tab));

			$s = $db->createTable($v_tablename, $v_tab, null , null, $db->DbName);
			if ($s && $db->haveNoLinks($v_tablename, $this)){
				//$this->initDataEntry($db);
			}
			$db->close();
		}
