	//get function uri
	public function getUri($function=null)
	{
		$out = "c=".strtolower($this->getName());
		if ($function){
			$t = explode("&", $function);
			$t[0] = "&f=".str_replace ('_', '-', $t[0]);
			$out .= implode('&', $t);
		}
		return "?".$out;
	}
