							$li = $v_table->addTr();
							IGKHtmlUtils::AddToggleAllCheckboxTh($li);
							$li->add("th", array("class"=>"fitw"))->Content = R::ngets("clDataBaseName");
							$li->add("th")->Content = IGK_HTML_SPACE;
							$li->add("th")->Content = IGK_HTML_SPACE;
					}
