	public function ca_drop_controller_ajx($ctrl=null, $reconnect=1)
	{
		$a = $ctrl? $ctrl: (($ctrl = igk_getr("clController")) ? $ctrl :  igk_getr("n"));

		if ($a)
		{
			$ctrl = igk_getctrl($a,false);
			if ($ctrl == null)
				return;

			$is_ajx = igk_is_ajx_demand();
			if (igk_qr_confirm())
			{

				// $doc = igk_get_last_rendered_document();
