						{
							$doc = igk_get_last_rendered_document();
							if ($doc===null)
								$doc = igk_app()->Doc;
							if (defined('IGK_CONFIG_PAGE'))
							igk_getconfigwebpagectrl()->View();

							$doc->body->RenderAJX();
							igk_exit();
						}
						else if ($reconnect){

							$ctrl->ClearSessionAndReconnect(false);
						}
					}
					else{
						igk_ilog("drop the controlleur failed : ".$a,__FUNCTION__);
					}
				}
				else{
					$this->msbox->addErrorr("err.dropctrlfailed");//""impossible de supprimer le controller. cela peut entrainer des incohÃ©rences");
