	protected function initTargetNode(){
		if(!$this->IsVisible)
			return null;
		$this->setParam(IGK_KEY_CSS_NOCLEAR, 1);
		$node =  igk_createNode("div")->setAttribute("class","igk-cnf-page fit igk-parentscroll igk-powered-viewer overflow-y-a");
