	function get_serverinfo(){
		$this->ConfigNode->clearChilds();
		$dv  = $this->ConfigNode->addDiv();
		$dv->addSectionTitle()->Content = "\$_SERVER";
		$tb = $dv->addTable();
		foreach($_SERVER as $k=>$v){
			$r = $tb->addTr();
			$r->addTd()->Content = $k;

			$r->addTd()->Content = $v;
		}
		$dv  = $this->ConfigNode->addDiv();
