	public function send_contactmail($fromName, $message=null)
	{
		$obj = igk_get_robj();

		$enode =  igk_createNode("div");
		$mail = new IGKMail();

		$this->initMailSetting();
		$this->init_mail_config($mail);



			$mail->addTo($this->App->Configs->mail_contact);
