	public function search($searchkey=null){
		$q = igk_getr("q",$searchkey);


		if (empty($q))
		{
			$this->m_search = null;
			$this->m_searchkey = null;
			$this->View();
			return;
		}
		$tab = $this->App->Doc->Theme->def->Attributes->ToArray();
		$out = array();
		foreach($tab as $k=>$v)
		{
			if (strstr($k, $q))
			{
				$out[$k]=$v;
			}
		}
		$this->m_search = $out;
		$this->m_searchkey = $q;
		$this->View();
	}
