		$frame->BoxContent->ClearChilds();
		$d = $frame->BoxContent->addDiv();
		$frm = $d->addForm();
		$frm["action"] = $this->getUri(__FUNCTION__);
		$d = $frm->addDiv();
		$d["class"] = "igk-form-group";

		$table = $d->addTable();
