	public static function Contains($ctrl){
		$i = self::getInstance();

		if ($ctrl && isset($i->m_ctrls[$ctrl->Name]))
		{
			return true;
		}
		return false;
	}
	public static function RegViewCtrl($ctrl, $handleevent=1){
		$i = self::getInstance();
		if ($i->m_ctrls == null){
