	///<summary>get installed plugin on server</summary>
	public function nop_i(){//
		if(strtolower(IGK_ROOT_SERVER) == strtolower(igk_io_baseUri()))
		{
			$tab = igk_db_table_select_where("tbigk_plugins");
			if ($tab){
				igk_wl($tab->RowCount);
			}
		}
		else
			igk_wl(IGK_HTML_CHAR_ZERO);
